<!--  -->
<template>
  <div
    class="go-to-top"
    @click="toTop"
    style="transform-origin: 0px 0px 0px; opacity: 1; transform: scale(1, 1);"
  ></div>
</template>

<script>
export default {
  data() {
    return {

    };
  },
  methods: {
    toTop() {
      let distance = Math.floor(window.scrollY / 50);

      let timer = setInterval(() => {
        if (document.documentElement.scrollTop > 0) {
          document.documentElement.scrollTop -= distance;
        } else {
          clearInterval(timer);
        }
      }, 1)

    }
  }
};
</script>


<style scoped>
.go-to-top {
  position: fixed;
  right: 15px;
  bottom: 60px;
  width: 60px;
  height: 60px;
  background: url("data:image/png;base64,")
    no-repeat 50%;
  background-size: 46px;
}
</style>